import React, { Component } from 'react'

export default class Message extends Component {
    constructor(){
        super()
        this.state = {
            message: 'Welcome, You'
        }
    }

    render() {
        return (
            <div>
                <h1>{this.state.message}</h1>
                <button>Click Me</button>
            </div>
        )
    }
}
